contracts/vaults/SpiceFiNFT4626.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/interfaces/IERC4626Upgradeable.sol";
import "@openzeppelin/contracts/utils/Multicall.sol";

import "../interfaces/IWETH.sol";
import "../interfaces/ISpiceFiNFT4626.sol";
import "../interfaces/IAggregatorVault.sol";

/**
 * @title Storage for SpiceFiNFT4626
 * @author Spice Finance Inc
 */
abstract contract SpiceFiNFT4626Storage {
    /// @notice withdrawal fees per 10_000 units
    uint256 public withdrawalFees;

    /// @notice Total shares
    uint256 public totalShares;

    /// @notice Mapping TokenId => Shares
    mapping(uint256 => uint256) public tokenShares;

    /// @notice Indicates whether the vault is verified or not
    bool public verified;

    /// @notice Token ID Pointer
    uint256 internal _tokenIdPointer;

    /// @notice Preview Metadata URI
    string internal _previewUri;

    /// @notice Metadata URI
    string internal _baseUri;

    /// @notice Revealed;
    bool internal _revealed;
}

/**
 * @title SpiceFiNFT4626
 * @author Spice Finance Inc
 */
contract SpiceFiNFT4626 is
    ISpiceFiNFT4626,
    IAggregatorVault,
    SpiceFiNFT4626Storage,
    UUPSUpgradeable,
    ERC721Upgradeable,
    PausableUpgradeable,
    AccessControlEnumerableUpgradeable,
    ReentrancyGuardUpgradeable,
    Multicall
{
    using SafeMathUpgradeable for uint256;
    using MathUpgradeable for uint256;
    using StringsUpgradeable for uint256;

    /*************/
    /* Constants */
    /*************/

    /// @notice WETH address
    address public constant WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;

    /// @notice Spice Multisig
    address public constant multisig =
        address(0x7B15f2B26C25e1815Dc4FB8957cE76a0C5319582);

    /// @notice Rebalance vault assets using ERC4626 client interface
    bytes32 public constant STRATEGIST_ROLE = keccak256("STRATEGIST_ROLE");

    /// @notice Contracts that funds can be sent to
    bytes32 public constant VAULT_ROLE = keccak256("VAULT_ROLE");

    /// @notice Contracts that receive fees
    bytes32 public constant ASSET_RECEIVER_ROLE =
        keccak256("ASSET_RECEIVER_ROLE");

    /// @notice Contracts allowed to deposit
    bytes32 public constant USER_ROLE = keccak256("USER_ROLE");

    /// @notice Spice role
    bytes32 public constant SPICE_ROLE = keccak256("SPICE_ROLE");

    /// @notice Max NFT Supply
    uint256 constant MAX_SUPPLY = 555;

    /// @notice Mint price
    uint256 constant MINT_PRICE = 0.08 ether;

    /**********/
    /* Errors */
    /**********/

    /// @notice Invalid address (e.g. zero address)
    error InvalidAddress();

    /// @notice Parameter out of bounds
    error ParameterOutOfBounds();

    /// @notice More than one NFT
    error MoreThanOne();

    /// @notice MAX_SUPPLY NFTs are minted
    error OutOfSupply();

    /// @notice User not owning token
    error InvalidTokenId();

    /// @notice Metadata revealed
    error MetadataRevealed();

    /// @notice Withdraw before reveal
    error WithdrawBeforeReveal();

    /// @notice Insufficient share balance
    error InsufficientShareBalance();

    /// @notice Slippage too high
    error SlippageTooHigh();

    /***************/
    /* Constructor */
    /***************/

    /// @notice SpiceFiNFT4626 constructor (for proxy)
    /// @param strategist_ Default strategist address
    /// @param assetReceiver_ Default asset receiver address
    /// @param withdrawalFees_ Default withdrawal fees
    function initialize(
        address strategist_,
        address assetReceiver_,
        uint256 withdrawalFees_
    ) public initializer {
        if (strategist_ == address(0)) {
            revert InvalidAddress();
        }
        if (assetReceiver_ == address(0)) {
            revert InvalidAddress();
        }
        if (withdrawalFees_ >= 10_000) {
            revert ParameterOutOfBounds();
        }

        __ERC721_init("Spice Finance", "SPICE");

        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(DEFAULT_ADMIN_ROLE, multisig);

        _setupRole(STRATEGIST_ROLE, strategist_);
        _setupRole(ASSET_RECEIVER_ROLE, assetReceiver_);

        withdrawalFees = withdrawalFees_;
    }

    /// @inheritdoc UUPSUpgradeable
    function _authorizeUpgrade(address newImplementation)
        internal
        override
        onlyRole(DEFAULT_ADMIN_ROLE)
    {}

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /// @notice set withdrawal fees
    /// @param withdrawalFees_ withdrawal fees
    function setWithdrawalFees(uint256 withdrawalFees_)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        if (withdrawalFees_ >= 10_000) {
            revert ParameterOutOfBounds();
        }

        withdrawalFees = withdrawalFees_;
    }

    /// @notice Sets preview uri
    /// @param previewUri New preview uri
    function setPreviewURI(string memory previewUri)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        if (_revealed) {
            revert MetadataRevealed();
        }

        _previewUri = previewUri;
    }

    /// @notice Sets base uri and reveal
    /// @param baseUri Metadata base uri
    function setBaseURI(string memory baseUri)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        _revealed = true;
        _baseUri = baseUri;
    }

    /// @notice set verified
    /// @param verified_ new verified value
    function setVerified(bool verified_) external onlyRole(SPICE_ROLE) {
        verified = verified_;
    }

    /// @notice trigger paused state
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /// @notice return to normal state
    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    /***********/
    /* Getters */
    /***********/

    /// @notice See {ISpiceFiNFT4626-asset}
    function asset() public pure returns (address) {
        return WETH;
    }

    /// @notice See {ISpiceFiNFT4626-totalAssets}
    function totalAssets() public view returns (uint256) {
        uint256 balance = IERC20Upgradeable(asset()).balanceOf(address(this));

        IERC4626Upgradeable vault;
        uint256 count = getRoleMemberCount(VAULT_ROLE);
        for (uint256 i; i != count; ) {
            vault = IERC4626Upgradeable(getRoleMember(VAULT_ROLE, i));
            balance += vault.previewRedeem(vault.balanceOf(address(this)));
            unchecked {
                ++i;
            }
        }
        return balance;
    }

    /// @notice See {ISpiceFiNFT4626-convertToShares}
    function convertToShares(uint256 assets) external view returns (uint256) {
        return _convertToShares(assets, MathUpgradeable.Rounding.Down);
    }

    /// @notice See {ISpiceFiNFT4626-convertToAssets}
    function convertToAssets(uint256 shares) external view returns (uint256) {
        return _convertToAssets(shares, MathUpgradeable.Rounding.Down);
    }

    /// @notice See {ISpiceFiNFT4626-maxDeposit}
    function maxDeposit(address) public view override returns (uint256) {
        return paused() ? 0 : type(uint256).max;
    }

    /// @notice See {ISpiceFiNFT4626-maxMint}
    function maxMint(address) public view override returns (uint256) {
        return paused() ? 0 : type(uint256).max;
    }

    /// @notice See {ISpiceFiNFT4626-maxWithdraw}
    function maxWithdraw(address) public view override returns (uint256) {
        uint256 balance = IERC20Upgradeable(asset()).balanceOf(address(this));
        return paused() ? 0 : balance.mulDiv(10_000 - withdrawalFees, 10_000);
    }

    /// @notice See {ISpiceFiNFT4626-maxRedeem}
    function maxRedeem(address) public view override returns (uint256) {
        uint256 balance = IERC20MetadataUpgradeable(asset()).balanceOf(
            address(this)
        );
        return
            paused()
                ? 0
                : _convertToShares(
                    balance.mulDiv(10_000 - withdrawalFees, 10_000),
                    MathUpgradeable.Rounding.Down
                );
    }

    /// @notice See {ISpiceFiNFT4626-previewDeposit}
    function previewDeposit(uint256 assets) public view returns (uint256) {
        return _convertToShares(assets, MathUpgradeable.Rounding.Down);
    }

    /// @notice See {ISpiceFiNFT4626-previewMint}
    function previewMint(uint256 shares) public view returns (uint256) {
        return _convertToAssets(shares, MathUpgradeable.Rounding.Up);
    }

    /// @notice See {ISpiceFiNFT4626-previewWithdraw}
    function previewWithdraw(uint256 assets)
        public
        view
        override
        returns (uint256)
    {
        return
            _convertToShares(
                assets.mulDiv(10_000, 10_000 - withdrawalFees),
                MathUpgradeable.Rounding.Up
            );
    }

    /// @notice See {ISpiceFiNFT4626-previewRedeem}
    function previewRedeem(uint256 shares)
        public
        view
        override
        returns (uint256)
    {
        return
            _convertToAssets(
                shares.mulDiv(10_000 - withdrawalFees, 10_000),
                MathUpgradeable.Rounding.Down
            );
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721Upgradeable, AccessControlEnumerableUpgradeable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    /// @notice See {IERC721Metadata-tokenURI}.
    function tokenURI(uint256 tokenId)
        public
        view
        override
        returns (string memory)
    {
        _requireMinted(tokenId);

        if (!_revealed) {
            return _previewUri;
        }

        string memory baseURI = _baseUri;
        return
            bytes(baseURI).length > 0
                ? string(abi.encodePacked(baseURI, tokenId.toString()))
                : "";
    }

    /******************/
    /* User Functions */
    /******************/

    /// See {ISpiceFiNFT4626-deposit}.
    function deposit(uint256 tokenId, uint256 assets)
        external
        whenNotPaused
        nonReentrant
        returns (uint256 shares)
    {
        // Compute number of shares to mint from current vault share price
        shares = previewDeposit(assets);

        if (assets > 0) {
            IERC20Upgradeable weth = IERC20Upgradeable(WETH);
            weth.transferFrom(msg.sender, address(this), assets);
        }

        _deposit(msg.sender, tokenId, assets, shares);
    }

    /// See {ISpiceFiNFT4626-mint}.
    function mint(uint256 tokenId, uint256 shares)
        external
        whenNotPaused
        nonReentrant
        returns (uint256 assets)
    {
        // Compute number of shares to mint from current vault share price
        assets = previewMint(shares);

        _deposit(msg.sender, tokenId, assets, shares);

        if (assets > 0) {
            IERC20Upgradeable weth = IERC20Upgradeable(WETH);
            weth.transferFrom(msg.sender, address(this), assets);
        }
    }

    /// See {ISpiceFiNFT4626-redeem}.
    function redeem(
        uint256 tokenId,
        uint256 shares,
        address receiver
    ) external whenNotPaused nonReentrant returns (uint256 assets) {
        if (tokenId == 0) {
            revert ParameterOutOfBounds();
        }
        if (shares == 0) {
            revert ParameterOutOfBounds();
        }
        if (receiver == address(0)) {
            revert InvalidAddress();
        }

        // compute redemption amount
        assets = previewRedeem(shares);

        // compute fee
        uint256 fees = _convertToAssets(shares, MathUpgradeable.Rounding.Down) -
            assets;

        _withdraw(msg.sender, tokenId, receiver, assets, shares, fees);
    }

    /// See {ISpiceFiNFT4626-withdraw}.
    function withdraw(
        uint256 tokenId,
        uint256 assets,
        address receiver
    ) external whenNotPaused nonReentrant returns (uint256 shares) {
        if (tokenId == 0) {
            revert ParameterOutOfBounds();
        }
        if (assets == 0) {
            revert ParameterOutOfBounds();
        }
        if (receiver == address(0)) {
            revert InvalidAddress();
        }

        // compute share amount
        shares = previewWithdraw(assets);

        // compute fee
        uint256 fees = _convertToAssets(
            shares - _convertToShares(assets, MathUpgradeable.Rounding.Up),
            MathUpgradeable.Rounding.Down
        );

        _withdraw(msg.sender, tokenId, receiver, assets, shares, fees);
    }

    /*****************************/
    /* Internal Helper Functions */
    /*****************************/

    function _convertToShares(uint256 assets, MathUpgradeable.Rounding rounding)
        internal
        view
        returns (uint256 shares)
    {
        uint256 _totalShares = totalShares;
        return
            (assets == 0 || _totalShares == 0)
                ? assets
                : assets.mulDiv(_totalShares, totalAssets(), rounding);
    }

    function _convertToAssets(uint256 shares, MathUpgradeable.Rounding rounding)
        internal
        view
        returns (uint256 assets)
    {
        uint256 _totalShares = totalShares;
        return
            (_totalShares == 0)
                ? shares
                : shares.mulDiv(totalAssets(), _totalShares, rounding);
    }

    function _mintInternal(address user) internal returns (uint256 tokenId) {
        if (balanceOf(user) > 0) {
            revert MoreThanOne();
        }

        if (_tokenIdPointer == MAX_SUPPLY) {
            revert OutOfSupply();
        }

        unchecked {
            tokenId = ++_tokenIdPointer;
        }

        IERC20Upgradeable weth = IERC20Upgradeable(WETH);
        address admin = getRoleMember(SPICE_ROLE, 0);
        weth.transferFrom(msg.sender, admin, MINT_PRICE);

        _mint(user, tokenId);
    }

    function _withdraw(
        address caller,
        uint256 tokenId,
        address receiver,
        uint256 assets,
        uint256 shares,
        uint256 fees
    ) internal {
        if (!_revealed) {
            revert WithdrawBeforeReveal();
        }
        if (ownerOf(tokenId) != caller) {
            revert InvalidTokenId();
        }

        if (tokenShares[tokenId] < shares) {
            revert InsufficientShareBalance();
        }

        totalShares -= shares;
        tokenShares[tokenId] -= shares;

        address feesAddr1 = getRoleMember(ASSET_RECEIVER_ROLE, 0);
        address feesAddr2 = getRoleMember(SPICE_ROLE, 0);
        uint256 fees1 = fees.div(2);

        IERC20Upgradeable weth = IERC20Upgradeable(WETH);
        weth.transfer(feesAddr1, fees1);
        weth.transfer(feesAddr2, fees.sub(fees1));
        weth.transfer(receiver, assets);

        emit Withdraw(caller, tokenId, receiver, assets, shares);
    }

    function _deposit(
        address caller,
        uint256 tokenId,
        uint256 assets,
        uint256 shares
    ) internal {
        require(
            getRoleMemberCount(USER_ROLE) == 0 || hasRole(USER_ROLE, caller),
            "caller is not enabled"
        );

        if (tokenId == 0) {
            // mints new NFT
            tokenId = _mintInternal(caller);
        } else if (ownerOf(tokenId) != caller) {
            revert InvalidTokenId();
        }

        tokenShares[tokenId] += shares;
        totalShares += shares;

        emit Deposit(caller, tokenId, assets, shares);
    }

    /// See {IAggregatorVault-deposit}
    function deposit(
        address vault,
        uint256 assets,
        uint256 minShares
    ) public nonReentrant onlyRole(STRATEGIST_ROLE) returns (uint256 shares) {
        _checkRole(VAULT_ROLE, vault);
        SafeERC20Upgradeable.safeIncreaseAllowance(
            IERC20MetadataUpgradeable(asset()),
            vault,
            assets
        );
        shares = IERC4626Upgradeable(vault).deposit(assets, address(this));

        if (minShares > shares) {
            revert SlippageTooHigh();
        }
    }

    /// See {IAggregatorVault-mint}
    function mint(
        address vault,
        uint256 shares,
        uint256 maxAssets
    ) public nonReentrant onlyRole(STRATEGIST_ROLE) returns (uint256 assets) {
        _checkRole(VAULT_ROLE, vault);
        uint256 assets_ = IERC4626Upgradeable(vault).previewMint(shares);
        SafeERC20Upgradeable.safeIncreaseAllowance(
            IERC20MetadataUpgradeable(asset()),
            vault,
            assets_
        );
        assets = IERC4626Upgradeable(vault).mint(shares, address(this));

        if (maxAssets < assets) {
            revert SlippageTooHigh();
        }
    }

    /// See {IAggregatorVault-withdraw}
    function withdraw(
        address vault,
        uint256 assets,
        uint256 maxShares
    ) public nonReentrant onlyRole(STRATEGIST_ROLE) returns (uint256 shares) {
        _checkRole(VAULT_ROLE, vault);
        shares = IERC4626Upgradeable(vault).withdraw(
            assets,
            address(this),
            address(this)
        );

        if (maxShares < shares) {
            revert SlippageTooHigh();
        }
    }

    /// See {IAggregatorVault-redeem}
    function redeem(
        address vault,
        uint256 shares,
        uint256 minAssets
    ) public nonReentrant onlyRole(STRATEGIST_ROLE) returns (uint256 assets) {
        _checkRole(VAULT_ROLE, vault);
        assets = IERC4626Upgradeable(vault).redeem(
            shares,
            address(this),
            address(this)
        );

        if (minAssets > assets) {
            revert SlippageTooHigh();
        }
    }
}

contracts/interfaces/ISpiceFiNFT4626.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

/**
 * @title ISpiceFiNFT4626
 * @author Spice Finance Inc
 */
interface ISpiceFiNFT4626 {
    event Deposit(
        address indexed sender,
        uint256 indexed tokenId,
        uint256 assets,
        uint256 shares
    );

    event Withdraw(
        address indexed sender,
        uint256 indexed tokenId,
        address indexed receiver,
        uint256 assets,
        uint256 shares
    );

    /// @notice Returns the address of the underlying token used for the Vault for accounting, depositing, and withdrawing.
    ///
    /// - MUST be an ERC-20 token contract.
    /// - MUST NOT revert.
    function asset() external view returns (address assetTokenAddress);

    /// @notice Returns the total amount of the underlying asset that is “managed” by Vault.
    ///
    /// - SHOULD include any compounding that occurs from yield.
    /// - MUST be inclusive of any fees that are charged against assets in the Vault.
    /// - MUST NOT revert.
    function totalAssets() external view returns (uint256 totalManagedAssets);

    /// @notice Returns the amount of shares that the Vault would exchange for the amount of assets provided, in an ideal
    /// scenario where all the conditions are met.
    ///
    /// - MUST NOT be inclusive of any fees that are charged against assets in the Vault.
    /// - MUST NOT show any variations depending on the caller.
    /// - MUST NOT reflect slippage or other on-chain conditions, when performing the actual exchange.
    /// - MUST NOT revert.
    ///
    /// NOTE: This calculation MAY NOT reflect the “per-user” price-per-share, and instead should reflect the
    /// “average-user’s” price-per-share, meaning what the average user should expect to see when exchanging to and
    /// from.
    function convertToShares(uint256 assets)
        external
        view
        returns (uint256 shares);

    /// @notice Returns the amount of assets that the Vault would exchange for the amount of shares provided, in an ideal
    /// scenario where all the conditions are met.
    ///
    /// - MUST NOT be inclusive of any fees that are charged against assets in the Vault.
    /// - MUST NOT show any variations depending on the caller.
    /// - MUST NOT reflect slippage or other on-chain conditions, when performing the actual exchange.
    /// - MUST NOT revert.
    ///
    /// NOTE: This calculation MAY NOT reflect the “per-user” price-per-share, and instead should reflect the
    /// “average-user’s” price-per-share, meaning what the average user should expect to see when exchanging to and
    /// from.
    function convertToAssets(uint256 shares)
        external
        view
        returns (uint256 assets);

    /// @notice Returns the maximum amount of the underlying asset that can be deposited into the Vault for the receiver,
    /// through a deposit call.
    ///
    /// - MUST return a limited value if receiver is subject to some deposit limit.
    /// - MUST return 2 ** 256 - 1 if there is no limit on the maximum amount of assets that may be deposited.
    /// - MUST NOT revert.
    function maxDeposit(address receiver)
        external
        view
        returns (uint256 maxAssets);

    /// @notice Allows an on-chain or off-chain user to simulate the effects of their deposit at the current block, given
    /// current on-chain conditions.
    ///
    /// - MUST return as close to and no more than the exact amount of Vault shares that would be minted in a deposit
    ///   call in the same transaction. I.e. deposit should return the same or more shares as previewDeposit if called
    ///   in the same transaction.
    /// - MUST NOT account for deposit limits like those returned from maxDeposit and should always act as though the
    ///   deposit would be accepted, regardless if the user has enough tokens approved, etc.
    /// - MUST be inclusive of deposit fees. Integrators should be aware of the existence of deposit fees.
    /// - MUST NOT revert.
    ///
    /// NOTE: any unfavorable discrepancy between convertToShares and previewDeposit SHOULD be considered slippage in
    /// share price or some other type of condition, meaning the depositor will lose assets by depositing.
    function previewDeposit(uint256 assets)
        external
        view
        returns (uint256 shares);

    /// @notice Deposits weth
    /// @param tokenId NFT token id
    /// @param assets The amount of weth being deposited
    /// @return shares The amount of receipt tokens minted
    function deposit(uint256 tokenId, uint256 assets)
        external
        returns (uint256 shares);

    /// @notice Returns the maximum amount of the Vault shares that can be minted for the receiver, through a mint call.
    /// - MUST return a limited value if receiver is subject to some mint limit.
    /// - MUST return 2 ** 256 - 1 if there is no limit on the maximum amount of shares that may be minted.
    /// - MUST NOT revert.
    function maxMint(address receiver)
        external
        view
        returns (uint256 maxShares);

    /// @notice Allows an on-chain or off-chain user to simulate the effects of their mint at the current block, given
    /// current on-chain conditions.
    ///
    /// - MUST return as close to and no fewer than the exact amount of assets that would be deposited in a mint call
    ///   in the same transaction. I.e. mint should return the same or fewer assets as previewMint if called in the
    ///   same transaction.
    /// - MUST NOT account for mint limits like those returned from maxMint and should always act as though the mint
    ///   would be accepted, regardless if the user has enough tokens approved, etc.
    /// - MUST be inclusive of deposit fees. Integrators should be aware of the existence of deposit fees.
    /// - MUST NOT revert.
    ///
    /// NOTE: any unfavorable discrepancy between convertToAssets and previewMint SHOULD be considered slippage in
    /// share price or some other type of condition, meaning the depositor will lose assets by minting.
    function previewMint(uint256 shares) external view returns (uint256 assets);

    /// @notice Deposits weth
    /// @param tokenId NFT token id
    /// @param shares The amount of receipt tokens to mint
    /// @return assets The amount of weth deposited
    function mint(uint256 tokenId, uint256 shares)
        external
        returns (uint256 assets);

    /// @notice Returns the maximum amount of the underlying asset that can be withdrawn from the owner balance in the
    /// Vault, through a withdraw call.
    ///
    /// - MUST return a limited value if owner is subject to some withdrawal limit or timelock.
    /// - MUST NOT revert.
    function maxWithdraw(address owner)
        external
        view
        returns (uint256 maxAssets);

    /// @notice Allows an on-chain or off-chain user to simulate the effects of their withdrawal at the current block,
    /// given current on-chain conditions.
    ///
    /// - MUST return as close to and no fewer than the exact amount of Vault shares that would be burned in a withdraw
    ///   call in the same transaction. I.e. withdraw should return the same or fewer shares as previewWithdraw if
    ///   called
    ///   in the same transaction.
    /// - MUST NOT account for withdrawal limits like those returned from maxWithdraw and should always act as though
    ///   the withdrawal would be accepted, regardless if the user has enough shares, etc.
    /// - MUST be inclusive of withdrawal fees. Integrators should be aware of the existence of withdrawal fees.
    /// - MUST NOT revert.
    ///
    /// NOTE: any unfavorable discrepancy between convertToShares and previewWithdraw SHOULD be considered slippage in
    /// share price or some other type of condition, meaning the depositor will lose assets by depositing.
    function previewWithdraw(uint256 assets)
        external
        view
        returns (uint256 shares);

    /// @notice Withdraw weth from the pool
    /// @param tokenId NFT token id
    /// @param assets The amount of weth being withdrawn
    /// @param receiver The account that will receive weth
    function withdraw(
        uint256 tokenId,
        uint256 assets,
        address receiver
    ) external returns (uint256 shares);

    /// @notice Returns the maximum amount of Vault shares that can be redeemed from the owner balance in the Vault,
    /// through a redeem call.
    ///
    /// - MUST return a limited value if owner is subject to some withdrawal limit or timelock.
    /// - MUST return balanceOf(owner) if owner is not subject to any withdrawal limit or timelock.
    /// - MUST NOT revert.
    function maxRedeem(address owner) external view returns (uint256 maxShares);

    /// @notice Allows an on-chain or off-chain user to simulate the effects of their redeemption at the current block,
    /// given current on-chain conditions.
    ///
    /// - MUST return as close to and no more than the exact amount of assets that would be withdrawn in a redeem call
    ///   in the same transaction. I.e. redeem should return the same or more assets as previewRedeem if called in the
    ///   same transaction.
    /// - MUST NOT account for redemption limits like those returned from maxRedeem and should always act as though the
    ///   redemption would be accepted, regardless if the user has enough shares, etc.
    /// - MUST be inclusive of withdrawal fees. Integrators should be aware of the existence of withdrawal fees.
    /// - MUST NOT revert.
    ///
    /// NOTE: any unfavorable discrepancy between convertToAssets and previewRedeem SHOULD be considered slippage in
    /// share price or some other type of condition, meaning the depositor will lose assets by redeeming.
    function previewRedeem(uint256 shares)
        external
        view
        returns (uint256 assets);

    /// @notice Withdraw weth from the pool
    /// @param tokenId NFT token id
    /// @param shares The amount of shares to redeem
    /// @param receiver The account that will receive weth
    function redeem(
        uint256 tokenId,
        uint256 shares,
        address receiver
    ) external returns (uint256 assets);
}

contracts/vaults/Bend4626.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/interfaces/IERC4626Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/MathUpgradeable.sol";

import "../interfaces/IWETH.sol";
import "../interfaces/IBendLendPool.sol";

/**
 * @title Storage for Bend4626
 * @author Spice Finance Inc
 */
abstract contract Bend4626Storage {
    /// @notice LendPool address
    address public poolAddress;

    /// @notice BToken address
    address public lpTokenAddress;

    /// @dev Token decimals
    uint8 internal _decimals;
}

/**
 * @title ERC4626 Wrapper for BendLendPool
 * @author Spice Finance Inc
 */
contract Bend4626 is
    Bend4626Storage,
    Initializable,
    ERC20Upgradeable,
    ReentrancyGuardUpgradeable
{
    using MathUpgradeable for uint256;

    /*************/
    /* Constants */
    /*************/

    /// @notice WETH address
    address public constant WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;

    uint256 public constant ONE_RAY = 1e27;

    /**********/
    /* Events */
    /**********/

    event Deposit(
        address indexed sender,
        address indexed owner,
        uint256 assets,
        uint256 shares
    );

    event Withdraw(
        address indexed sender,
        address indexed receiver,
        address indexed owner,
        uint256 assets,
        uint256 shares
    );

    /**********/
    /* Errors */
    /**********/

    /// @notice Invalid address (e.g. zero address)
    error InvalidAddress();

    /// @notice Parameter out of bounds
    error ParameterOutOfBounds();

    /***************/
    /* Constructor */
    /***************/

    /// @notice Bend4626 constructor (for proxy)
    /// @param name_ Receipt token name
    /// @param symbol_ Receipt token symbol
    /// @param poolAddress_ LendPool address
    /// @param lpTokenAddress_ BToken address
    function initialize(
        string calldata name_,
        string calldata symbol_,
        address poolAddress_,
        address lpTokenAddress_
    ) external initializer {
        if (poolAddress_ == address(0)) {
            revert InvalidAddress();
        }
        if (lpTokenAddress_ == address(0)) {
            revert InvalidAddress();
        }

        __ERC20_init(name_, symbol_);

        poolAddress = poolAddress_;

        uint8 decimals_;
        try
            IERC20MetadataUpgradeable(address(lpTokenAddress_)).decimals()
        returns (uint8 value) {
            decimals_ = value;
        } catch {
            decimals_ = super.decimals();
        }

        lpTokenAddress = lpTokenAddress_;
        _decimals = decimals_;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /***********/
    /* Getters */
    /***********/

    /// @notice See {IERC20Metadata-decimals}.
    function decimals() public view override returns (uint8) {
        return _decimals;
    }

    /// @notice Get underlying token address
    function asset() external pure returns (address) {
        return WETH;
    }

    /// @notice See {IERC4626-totalAssets}
    function totalAssets() public view returns (uint256) {
        return IERC20Upgradeable(lpTokenAddress).balanceOf(address(this));
    }

    /// @notice See {IERC4626-convertToShares}
    function convertToShares(uint256 assets) external view returns (uint256) {
        return _convertToShares(assets, MathUpgradeable.Rounding.Down);
    }

    /// @notice See {IERC4626-convertToAssets}
    function convertToAssets(uint256 shares) external view returns (uint256) {
        return _convertToAssets(shares, MathUpgradeable.Rounding.Down);
    }

    /// @notice See {IERC4626-maxDeposit}
    function maxDeposit(address) external pure returns (uint256) {
        return type(uint256).max;
    }

    /// @notice See {IERC4626-maxMint}
    function maxMint(address) external pure returns (uint256) {
        return type(uint256).max;
    }

    /// @notice See {IERC4626-maxWithdraw}
    function maxWithdraw(address owner) external view returns (uint256) {
        return
            _convertToAssets(balanceOf(owner), MathUpgradeable.Rounding.Down);
    }

    /// @notice See {IERC4626-maxRedeem}
    function maxRedeem(address owner) external view returns (uint256) {
        return balanceOf(owner);
    }

    /// @notice See {IERC4626-previewDeposit}
    function previewDeposit(uint256 assets) public view returns (uint256) {
        return _convertToShares(assets, MathUpgradeable.Rounding.Down);
    }

    /// @notice See {IERC4626-previewMint}
    function previewMint(uint256 shares) public view returns (uint256) {
        return _convertToAssets(shares, MathUpgradeable.Rounding.Up);
    }

    /// @notice See {IERC4626-previewWithdraw}
    function previewWithdraw(uint256 assets) public view returns (uint256) {
        return _convertToShares(assets, MathUpgradeable.Rounding.Up);
    }

    /// @notice See {IERC4626-previewRedeem}
    function previewRedeem(uint256 shares) public view returns (uint256) {
        return _convertToAssets(shares, MathUpgradeable.Rounding.Down);
    }

    /******************/
    /* User Functions */
    /******************/

    /// @notice Deposits weth into Bend pool and receive receipt tokens
    /// @param assets The amount of weth being deposited
    /// @param receiver The account that will receive the receipt tokens
    /// @return shares The amount of receipt tokens minted
    function deposit(uint256 assets, address receiver)
        external
        nonReentrant
        returns (uint256 shares)
    {
        if (assets == 0) {
            revert ParameterOutOfBounds();
        }
        if (receiver == address(0)) {
            revert InvalidAddress();
        }

        shares = previewDeposit(assets);

        _deposit(assets, shares, receiver);
    }

    /// @notice Deposits weth into Bend pool and receive receipt tokens
    /// @param shares The amount of receipt tokens to mint
    /// @param receiver The account that will receive the receipt tokens
    /// @return assets The amount of weth deposited
    function mint(uint256 shares, address receiver)
        external
        nonReentrant
        returns (uint256 assets)
    {
        if (shares == 0) {
            revert ParameterOutOfBounds();
        }
        if (receiver == address(0)) {
            revert InvalidAddress();
        }

        assets = previewMint(shares);

        _deposit(assets, shares, receiver);
    }

    /// @notice Withdraw weth from the pool
    /// @param assets The amount of weth being withdrawn
    /// @param receiver The account that will receive weth
    /// @param owner The account that will pay receipt tokens
    /// @return shares The amount of shares burnt
    function withdraw(
        uint256 assets,
        address receiver,
        address owner
    ) external nonReentrant returns (uint256 shares) {
        if (receiver == address(0)) {
            revert InvalidAddress();
        }
        if (assets == 0) {
            revert ParameterOutOfBounds();
        }

        shares = previewWithdraw(assets);

        _withdraw(msg.sender, receiver, owner, assets, shares);
    }

    /// @notice Withdraw weth from the pool
    /// @param shares The amount of receipt tokens being burnt
    /// @param receiver The account that will receive weth
    /// @param owner The account that will pay receipt tokens
    /// @return assets The amount of assets redeemed
    function redeem(
        uint256 shares,
        address receiver,
        address owner
    ) external nonReentrant returns (uint256 assets) {
        if (receiver == address(0)) {
            revert InvalidAddress();
        }
        if (shares == 0) {
            revert ParameterOutOfBounds();
        }

        assets = previewRedeem(shares);

        _withdraw(msg.sender, receiver, owner, assets, shares);
    }

    /*****************************/
    /* Internal Helper Functions */
    /*****************************/

    /// @dev Get estimated share amount for assets
    /// @param assets Asset token amount
    /// @param rounding Rounding mode
    /// @return shares Share amount
    function _convertToShares(uint256 assets, MathUpgradeable.Rounding rounding)
        internal
        view
        returns (uint256 shares)
    {
        uint256 supply = totalSupply();
        return
            (assets == 0 || supply == 0)
                ? assets
                : assets.mulDiv(supply, totalAssets(), rounding);
    }

    /// @dev Get estimated share amount for assets
    /// @param shares Share amount
    /// @param rounding Rounding mode
    /// @return assets Asset token amount
    function _convertToAssets(uint256 shares, MathUpgradeable.Rounding rounding)
        internal
        view
        returns (uint256 assets)
    {
        uint256 supply = totalSupply();
        return
            (supply == 0)
                ? shares
                : shares.mulDiv(totalAssets(), supply, rounding);
    }

    /// @dev Deposit/mint common workflow.
    function _deposit(
        uint256 assets,
        uint256 shares,
        address receiver
    ) internal {
        // load weth
        IWETH weth = IWETH(WETH);

        // receive weth from msg.sender
        weth.transferFrom(msg.sender, address(this), assets);

        // approve weth deposit into underlying marketplace
        weth.approve(poolAddress, assets);

        // deposit into underlying marketplace
        IBendLendPool(poolAddress).deposit(WETH, assets, address(this), 0);

        // Mint receipt tokens to receiver
        _mint(receiver, shares);

        emit Deposit(msg.sender, receiver, assets, shares);
    }

    /// @dev Withdraw/redeem common workflow.
    function _withdraw(
        address caller,
        address receiver,
        address owner,
        uint256 assets,
        uint256 shares
    ) internal {
        if (caller != owner) {
            _spendAllowance(owner, caller, shares);
        }

        // Burn receipt tokens from owner
        _burn(owner, shares);

        // get lp token contract
        IERC20Upgradeable bToken = IERC20Upgradeable(lpTokenAddress);

        // approve AToken's withdraw from the pool
        bToken.approve(poolAddress, assets);

        // withdraw weth from the pool and send it to `receiver`
        IBendLendPool(poolAddress).withdraw(WETH, assets, receiver);

        emit Withdraw(msg.sender, receiver, owner, assets, shares);
    }
}

contracts/interfaces/IBendLendPool.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.17;

import "../types/bend/DataTypes.sol";

interface IBendLendPool {
    /**
     * @dev Deposits an `amount` of underlying asset into the reserve, receiving in return overlying bTokens.
     * - E.g. User deposits 100 USDC and gets in return 100 bUSDC
     * @param reserve The address of the underlying asset to deposit
     * @param amount The amount to be deposited
     * @param onBehalfOf The address that will receive the bTokens, same as msg.sender if the user
     *   wants to receive them on his own wallet, or a different address if the beneficiary of bTokens
     *   is a different wallet
     * @param referralCode Code used to register the integrator originating the operation, for potential rewards.
     *   0 if the action is executed directly by the user, without any middle-man
     **/
    function deposit(
        address reserve,
        uint256 amount,
        address onBehalfOf,
        uint16 referralCode
    ) external;

    /**
     * @dev Withdraws an `amount` of underlying asset from the reserve, burning the equivalent bTokens owned
     * E.g. User has 100 bUSDC, calls withdraw() and receives 100 USDC, burning the 100 bUSDC
     * @param reserve The address of the underlying asset to withdraw
     * @param amount The underlying amount to be withdrawn
     *   - Send the value type(uint256).max in order to withdraw the whole bToken balance
     * @param to Address that will receive the underlying, same as msg.sender if the user
     *   wants to receive it on his own wallet, or a different address if the beneficiary is a
     *   different wallet
     * @return The final amount withdrawn
     **/
    function withdraw(
        address reserve,
        uint256 amount,
        address to
    ) external returns (uint256);

    /**
     * @dev Returns the state and configuration of the reserve
     * @param asset The address of the underlying asset of the reserve
     * @return The state of the reserve
     **/
    function getReserveData(address asset)
        external
        view
        returns (DataTypes.ReserveData memory);
}

contracts/types/bend/DataTypes.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

library DataTypes {
    struct ReserveData {
        //stores the reserve configuration
        ReserveConfigurationMap configuration;
        //the liquidity index. Expressed in ray
        uint128 liquidityIndex;
        //variable borrow index. Expressed in ray
        uint128 variableBorrowIndex;
        //the current supply rate. Expressed in ray
        uint128 currentLiquidityRate;
        //the current variable borrow rate. Expressed in ray
        uint128 currentVariableBorrowRate;
        uint40 lastUpdateTimestamp;
        //tokens addresses
        address lpTokenAddress;
        address debtTokenAddress;
        //address of the interest rate strategy
        address interestRateAddress;
        //the id of the reserve. Represents the position in the list of the active reserves
        uint8 id;
    }

    struct ReserveConfigurationMap {
        //bit 0-15: LTV
        //bit 16-31: Liq. threshold
        //bit 32-47: Liq. bonus
        //bit 48-55: Decimals
        //bit 56: Reserve is active
        //bit 57: reserve is frozen
        //bit 58: borrowing is enabled
        //bit 59: stable rate borrowing enabled
        //bit 60-63: reserved
        //bit 64-79: reserve factor
        uint256 data;
    }
}

